mapper.select:
SELECT COUNT(*)
FROM cart
WHERE prdNo=#{prdNo} AND memId=#{memId}

mapper.update:
UPDATE cart
SET cartQty = cartQty + #{cartQty}
WHERE memId=#{memId} AND prdNo=#{prdNo}

UPDATE cart
SET cartQty = #{cartQty}
WHERE cartNo = #{cartNo}

mapper.insert:
INSERT INTO order_product (ordNo, prdNo, ordQty)
SELECT #{ordNo}, prdNo, cartQty 
FROM cart WHERE memId=#{memId}
